import React, { useEffect, useState } from 'react';
import apis from '../api/index';
import '../css/HardwarePage.css';

function HardwareTable(){
    const [hardware, setHardwares] = useState([])

    useEffect(() => {
        const fetchHardwares = async () => {
        try {
            const response = await apis.getAllHardware();
            setHardwares(response.data); 
        } catch (error) {
            console.error('Error fetching agents:', error);
        }
        };

        fetchHardwares();
    }, []);

    return (
        <div className="agents-table-container">
          <p>Hardware Page</p>
          <table>
            <thead>
              <tr>
                <th>Agent ID</th>
                <th>CPU Name</th>
                <th>Cores</th>
                <th>Time</th>
              </tr>
            </thead>
            <tbody>
              {hardware.map((item) => (
                <tr key={item._id}>
                  <td>{item.agent_id}</td>
                  <td>{item.cpu.name}</td>
                  <td>{item.cpu.cores}</td>
                  <td><td>{formatTime(item.scan.time)}</td></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );

}

function formatTime(time) {
    const options = {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
      timeZone: 'UTC',
    };

    const formattedTime = new Date(time).toLocaleString('en-US', options);
    return formattedTime;
}

export default HardwareTable;
